{
    "class": "AS3",
    "action": "deploy",
    "persist": true,
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.6.0",
        "id": "custom_test",
        "label": "custom_test",
        "remark": "HTTPS L4 APP",
        "custom_test": {
            "class": "Tenant",
            "custom_test_app": {
                "class": "Application",
                "template": "l4",
                "serviceMain": {
                    "class": "Service_L4",
                    "persistenceMethods": [
                        {
                            "use": "custom_test_persistence"
                        }
                    ],
                    "virtualPort": 8283,
                    "virtualAddresses": [
                        "10.1.1.1"
                    ],
                    "layer4": "tcp",
                    "profileL4": {
                        "use": "custom_test_fastL4_tcp"
                    },
                    "pool": "custom_test_pool",
                    "snat": {
                        "bigip": "/Common/SNAT-POOL"
                    }
                },
                "custom_test_persistence": {
                    "class": "Persist",
                    "persistenceMethod": "source-address",
                    "duration": 1800
                },
                "custom_test_fastL4_tcp": {
                    "class": "L4_Profile",
                    "idleTimeout": 300,
                    "looseClose": true,
                    "looseInitialization": true,
                    "resetOnTimeout": false,
                    "tcpCloseTimeout": 30,
                    "tcpHandshakeTimeout": 30
                },
                "custom_test_pool": {
                    "class": "Pool",
                    "loadBalancingMode": "round-robin",
                    "slowRampTime": 300,
                    "monitors": [
                        {
                            "use": "custom_test_mon"
                        }
                    ],
                    "minimumMembersActive": 1,
                    "members": [
                        {
                            "servicePort": 8283,
                            "serverAddresses": [
                                "10.2.2.2"
                            ],
                            "priorityGroup": 200
                        },
                        {
                            "servicePort": 8283,
                            "serverAddresses": [
                                "10.2.2.3"
                            ],
                            "priorityGroup": 100
                        }
                    ]
                },
                "custom_test_mon": {
                    "class": "Monitor",
                    "monitorType": "https",
                    "send": "HEAD / HTTP/1.0rnrn",
                    "receive": "HTTP/1.",
                    "interval": 3,
                    "timeout": 10
                }
            }
        }
    }
}

