    "label": "Sample 3",
    "remark": "TCP load-balanced to ICAP with custom monitor",
    "Sample_03": {
      "class": "Tenant",
      "A1": {
        "class": "Application",
        "template": "tcp",
        "serviceMain": {
          "class": "Service_TCP",
          "virtualAddresses": [
            "10.0.5.10"
          ],
          "virtualPort": 1344,
          "pool": "svc_pool"
        },
        "svc_pool": {
          "class": "Pool",
          "monitors": [
            {
              "use": "icap_monitor"
            }
          ],
          "members": [
            {
              "servicePort": 1344,
              "serverAddresses": [
                "192.0.5.10",
                "192.0.5.11"
              ]
            }
          ]
        },
        "icap_monitor": {
          "class": "Monitor",
          "monitorType": "tcp",
          "send": "OPTIONS icap://icap.example.net/ ICAP/1.0\r\nUser-Agent: f5-ADC\r\n\r\n",
          "receive": "ICAP/1.0 200 OK",
          "adaptive": false
        }
      }
    }
  }
}

