{
    "class": "ADC",
    "schemaVersion": "3.9.0",
    "id": "TEST_GSLB_Domain",
    "label": "GSLB Configuration",
    "remark": "A Sample Configuration of GSLB",
    "controls": {
        "class": "Controls",
        "trace": true,
        "logLevel": "debug"
    },
    "TEST_GSLB_DOMAIN": {
        "class": "Tenant",
        "label": "Variations of GSLB Domain",
        "TEST_GSLB_Domain": {
            "class": "Application",
            "template": "generic",
            "domainMinimum": {
                "class": "GSLB_Domain",
                "domainName": "test.min.com",
                "resourceRecordType": "A"
            },
            "domainA": {
                "class": "GSLB_Domain",
                "enabled": true,
                "domainName": "lab.local",
                "aliases": [
                    "test1.a",
                    "test2.a*",
                    "test3.a?"
                ],
                "resourceRecordType": "A",
                "poolLbMode": "global-availability"
            },
            "domainAAAA": {
                "class": "GSLB_Domain",
                "domainName": "aaaa.lab.*",
                "aliases": [
                    "test1.aaaa?",
                    "test2.aaaa"
                ],
                "resourceRecordType": "AAAA",
                "poolLbMode": "ratio"
            },
            "domainCNAME": {
                "class": "GSLB_Domain",
                "domainName": "?cname.lab.local",
                "aliases": [
                    "test1.cname?",
                    "test2.cname*"
                ],
                "resourceRecordType": "CNAME"
            },
            "domainMX": {
                "class": "GSLB_Domain",
                "enabled": false,
                "domainName": "?mx.lab.local.*",
                "aliases": [
                    "?test1.mx.*",
                    "test2.mx"
                ],
                "resourceRecordType": "MX",
                "poolLbMode": "topology"
            }
        }
    }
}

{
