                    {
                        "matchType": "isp",
                        "matchOperator": "equals",
                        "matchValue": "Comcast"
                    },
                    {
                        "matchType": "region",
                        "matchOperator": "not-equals",
                        "matchValue": {
                            "bigip": "/Common/def_region"
                        }
                    },
                    {
                        "matchType": "state",
                        "matchOperator": "equals",
                        "matchValue": "US/New Hampshire"
                    },
                    {
                        "matchType": "state",
                        "matchOperator": "equals",
                        "matchValue": "US/Pennsylvania"
                    },
                    {
                        "matchType": "subnet",
                        "matchOperator": "equals",
