        "remark": "L4 App",
        "{{tenant_name}}": {
            "class": "Tenant",
            "{{service_name}}": {
                "class": "Application",
                "template": "l4",
                "serviceMain": {
                    "class": "Service_L4",
                    "persistenceMethods": [
                        {
                            "use": "{{tenant_name}}_persistence"
                        }
