                    "idleTimeout": 300,
                    "looseClose": true,
                    "looseInitialization": true,
                    "resetOnTimeout": false,
                    "tcpCloseTimeout": 30,
                    "tcpHandshakeTimeout": 30
                },
                "{{tenant_name}}_pool": {
                    "class": "Pool",
                    "loadBalancingMode": "round-robin",
                    "slowRampTime": 300,
                    "monitors": [
                        {
                            "use": "{{tenant_name}}_monitor"
                        }
                    ],
                    "minimumMembersActive": 1,
                    "members": [
                        {
                            "servicePort": 22,
                            "serverAddresses": [
                                "10.2.2.101"
                            ],
                            "priorityGroup": 200
                        },
                        {
                            "servicePort": 22,
                            "serverAddresses": [
                                "10.2.2.102"
                            ],
                            "priorityGroup": 100
                        }
                    ]
                },
                "{{tenant_name}}_monitor": {
                    "class": "Monitor",
                    "monitorType": "https",
                    "send": "HEAD / HTTP/1.0rnrn",
                    "receive": "HTTP/1.",
                    "interval": 3,
                    "timeout": 10
                }
            }
        }
    }
